void TileMap::update()
{
    
}

void TileMap::draw()
{
    for (int i=0; i<30; i++)
    {
        for (int j=0; j<120; j++)
        {
            switch (this->arr[i][j])
            {
                case 0:
                    td_drawPoint(j, i, '.', TD_COLOR_WHITE, TD_COLOR_DEFAULT);
                    break;

                case 1:
                    td_drawPoint(j, i, '#', TD_COLOR_WHITE, TD_COLOR_WHITE);
                    break;
            }
        }
    }
}

bool TileMap::isWall(Vector2i coords)
{
    if (this->arr.at(coords.y).at(coords.x) == 1)
    {
        return true;
    }
    
    return false;
}